import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';
import './visuals.scss';

function Visuals() {
  const [chartData, setChartData] = useState({
    datasets: [
      { budget: [], expense: [], backgroundColor: [] },
    ],
    labels1: [],
    labels2: [],
  });

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataExists, setDataExists] = useState(false);
  const [selectedMonth, setSelectedMonth] = useState('');
  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December',
  ];

  function getRandomColor(previousColors) {
    const letters = '0123456789ABCDEF';
    let color;

    do {
      color = '#';
      for (let i = 0; i < 6; i++) {
        color += letters[Math.floor(Math.random() * 16)];
      }
    } while (previousColors.includes(color));

    return color;
  }

  const fetchData = async (url, updateFunction) => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    try {
      const res = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.data && res.data.length > 0) {
        const updatedData = { ...chartData };
        for (let i = 0; i < res.data.length; i++) {
          updatedData.datasets[0].budget[i] = res.data[i].budget;
          updatedData.datasets[0].expense[i] = res.data[i].expense;
          updatedData.datasets[0].backgroundColor[i] = getRandomColor(chartData.datasets[0].backgroundColor);
          updateFunction(updatedData, res.data[i].category);
        }
        setChartData(updatedData);
        setDataExists(true);
        setDataLoaded(true);
      } else {
        setDataExists(false);
        setDataLoaded(true);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    if (selectedMonth) {
      const url = `http://localhost:3002/get-budgets/${localStorage.getItem('userId')}?month=${selectedMonth}`;
      fetchData(url, (data, category) => {
        data.labels1.push(category);
      });
    }
  }, [selectedMonth]);

  useEffect(() => {
    if (selectedMonth) {
      const url = `http://localhost:3002/get-expenses/${localStorage.getItem('userId')}?month=${selectedMonth}`;
      fetchData(url, (data, category) => {
        data.labels2.push(category);
      });
    }
  }, [selectedMonth]);

  const createChart = (chartRef, type, labels, budget, expenses) => {
    if (chartRef.current) {
      const chart = new Chart(chartRef.current, {
        type: type,
        data: {
          labels: labels,
          datasets: [
            {
              label: 'Budget',
              data: budget,
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              borderColor: 'rgba(75, 192, 192, 1)',
              borderWidth: 1,
            },
            {
              label: 'Expenses',
              data: expenses,
              backgroundColor: 'rgba(255, 99, 132, 0.2)',
              borderColor: 'rgba(255, 99, 132, 1)',
              borderWidth: 1,
            },
          ],
        },
        options: {
          scales: { x: { stacked: false }, y: { stacked: false } },
        },
      });

      return () => {
        chart.destroy();
      };
    }
  };

  const chartRef = useRef(null);
  const expenseRef = useRef(null);
  const chart1Ref = useRef(null);
  const chart2Ref = useRef(null);
  const chart3Ref = useRef(null);
  const chart4Ref = useRef(null);
  const chart5Ref = useRef(null);

  useEffect(() => createChart(chartRef, 'polarArea', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(expenseRef, 'polarArea', chartData.labels2, chartData.datasets[0].expense, chartData.datasets[0].budget), [chartData]);
  useEffect(() => createChart(chart1Ref, 'bar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart2Ref, 'radar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart3Ref, 'doughnut', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart4Ref, 'doughnut', chartData.labels1, chartData.datasets[0].expense, chartData.datasets[0].budget), [chartData]);
  useEffect(() => createChart(chart5Ref, 'bar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);

  const renderCharts = () => (
    <section>
      <section className="chart-container">
        <article className="chart">
          <h2>PolarArea Chart - Budget</h2>
          <canvas ref={chartRef} />
        </article>
        <article className="chart">
          <h2>PolarArea Chart - Expenses</h2>
          <canvas ref={expenseRef} />
        </article>
        <article className="chart">
          <h2>Grouped Bar Chart</h2>
          <canvas ref={chart1Ref} />
        </article>
        <article className="chart">
          <h2>Radar Chart</h2>
          <canvas ref={chart2Ref} />
        </article>
      </section>
      <section className="chart-container">
        <article className="chart">
          <h2>Donut Chart - Budgets</h2>
          <canvas ref={chart3Ref} />
        </article>
        <article className="chart">
          <h2>Donut Chart - Expenses</h2>
          <canvas ref={chart4Ref} />
        </article>
        <article className="chart">
          <h2>Combo Chart - Expenses</h2>
          <canvas ref={chart5Ref} />
        </article>
      </section>
    </section>
  );

  return (
    <main className="visuals-container">
      <div className="controls">
        <label htmlFor="months">Select Month:</label>
        <select id="months" onChange={(e) => setSelectedMonth(e.target.value)} value={selectedMonth}>
          <option value="">Select Month</option>
          {months.map((month) => (
            <option key={month} value={month}>{month}</option>
          ))}
        </select>
      </div>
      {dataLoaded ? (
        dataExists ? renderCharts() : <p>There is no sufficient data, please create some to display the charts.</p>
      ) : <p>Loading...</p>}
    </main>
  );
}

export default Visuals;
