import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';
import './visuals.scss';

function Visuals() {
  const [chartData, setChartData] = useState({ budget: [], expense: [], categories: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [selectedMonth, setSelectedMonth] = useState('');
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

  const fetchData = async (endpoint) => {
    setIsLoading(true);
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/${endpoint}/${userId}?month=${selectedMonth}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data && res.data.length > 0) {
        return res.data;
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setIsLoading(false);
    }
    return [];
  };

  useEffect(() => {
    const loadChartData = async () => {
      if (selectedMonth) {
        const budgetData = await fetchData('get-budgets');
        const expenseData = await fetchData('get-expenses');
        setChartData({
          budget: budgetData.map(d => d.value),
          expense: expenseData.map(d => d.value),
          categories: budgetData.map(d => d.category),
        });
      }
    };
    loadChartData();
  }, [selectedMonth]);

  const chartConfig = useMemo(() => {
    const chartRef = useRef(null);
  const expenseRef = useRef(null);
  const chart1Ref = useRef(null);
  const chart2Ref = useRef(null);
  const chart3Ref = useRef(null);
  const chart4Ref = useRef(null);
  const chart5Ref = useRef(null);

  useEffect(() => createChart(chartRef, 'polarArea', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(expenseRef, 'polarArea', chartData.labels2, chartData.datasets[0].expense, chartData.datasets[0].budget), [chartData]);
  useEffect(() => createChart(chart1Ref, 'bar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart2Ref, 'radar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart3Ref, 'doughnut', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  useEffect(() => createChart(chart4Ref, 'doughnut', chartData.labels1, chartData.datasets[0].expense, chartData.datasets[0].budget), [chartData]);
  useEffect(() => createChart(chart5Ref, 'bar', chartData.labels1, chartData.datasets[0].budget, chartData.datasets[0].expense), [chartData]);
  }, [chartData]);

  const renderCharts = () => {
<section>
      <section className="chart-container">
        <article className="chart">
          <h2>PolarArea Chart - Budget</h2>
          <canvas ref={chartRef} />
        </article>
        <article className="chart">
          <h2>PolarArea Chart - Expenses</h2>
          <canvas ref={expenseRef} />
        </article>
        <article className="chart">
          <h2>Grouped Bar Chart</h2>
          <canvas ref={chart1Ref} />
        </article>
        <article className="chart">
          <h2>Radar Chart</h2>
          <canvas ref={chart2Ref} />
        </article>
      </section>
      <section className="chart-container">
        <article className="chart">
          <h2>Donut Chart - Budgets</h2>
          <canvas ref={chart3Ref} />
        </article>
        <article className="chart">
          <h2>Donut Chart - Expenses</h2>
          <canvas ref={chart4Ref} />
        </article>
        <article className="chart">
          <h2>Combo Chart - Expenses</h2>
          <canvas ref={chart5Ref} />
        </article>
      </section>
    </section>
  };

  return (
    <main className="visuals-container">
      <div className="controls">
        <label htmlFor="months">Select Month:</label>
        <select id="months" onChange={(e) => setSelectedMonth(e.target.value)} value={selectedMonth}>
          <option value="">Select Month</option>
          {months.map((month) => (
            <option key={month} value={month}>{month}</option>
          ))}
        </select>
      </div>
      {isLoading ? <p>Loading...</p> : renderCharts()}
    </main>
  );
}

export default Visuals;
